import React from 'react';


class Header extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            Name:"Shah",
            course: "Adit",
            greet:'Welcome to React World üåè'  

        }
    }

    action(){
        this.setState(
            {
                greet:'Wow wonderful your just subscribe us now üî•' 
            }
        )
    }




   render(){
       return(
           <div>
               <h1>{this.state.greet}</h1>
               <h1>Hello {this.state.Name}</h1>
               <h1>Your Course is {this.state.course}</h1>
               <button onClick={()=> this.action()} >Subscribe now</button>
           </div>
       );
   }
}


export default Header;
