import React, { useState } from "react";
import "../index.css";

function Counter() {
  let intialCount = 0;
  let [count, setCount] = useState(intialCount);
  if (count < 0) {
    count = 0;
  }
  let btn = {
    color: "white",
    fontWeight: "bolder",
    outline: "none",
    cursor: "pointer",
    padding: "14px 20px",
    marginLeft: "20px",
    fontSize: "20px",
    border: "none",
    boxShadow: "5px 5px 10px black",
  };

  return (
    <div
      style={{
        boxShadow: "0px 0px 10px black",
        padding: "20px",
        marginTop: "50px",
        backgroundColor: "seagreen",
      }}
    >
      <h1>Counter App in React Js ðŸ”¥ </h1>
      <h1>{count}</h1>
      <button
        style={btn}
        className="incre btn"
        onClick={() => setCount(count + 1)}
      >
        Increment
      </button>
      <button
        style={btn}
        className="reset btn"
        onClick={() => setCount(intialCount)}
      >
        Reset
      </button>
      <button
        style={btn}
        className="decre btn"
        onClick={() => setCount(count - 1)}
      >
        Decrement
      </button>
    </div>
  );
}

export default Counter;
