import React, { useState } from "react";

function Counter() {
  let intialCount = 0;
  const [count, setCount] = useState(intialCount);
  let btn = {
    color: "white",
    fontWeight: "bolder",
    outline: "none",
    cursor: "pointer",
    padding: "14px 20px",
    marginLeft: "20px",
    border: "none",
    boxShadow: "5px 5px 5px 10px black",
  };

  return (
    <div>
      <h2>{count}</h2>
      <button style={btn} onClick={() => setCount(count + 1)}>
        Increment
      </button>
      <button style={btn} onClick={() => setCount(intialCount)}>
        Reset
      </button>
      <button style={btn} onClick={() => setCount(count - 1)}>
        Decrement
      </button>
    </div>
  );
}

export default Counter;
